import { useState } from "react";
import Button from "../Components/Button";
function CountdownSection(){
    const [timeLeft, setTimeLeft] = useState(seconds);

  useEffect(() => {
    // Exit if countdown reaches 0
    if (timeLeft === 0) return;

    // Create an interval to decrement the time every second
    const timer = setInterval(() => {
      setTimeLeft(timeLeft - 1);
    }, 1000);

    // Clean up the interval when component unmounts or when time reaches 0
    return () => clearInterval(timer);
  }, [timeLeft]);

  // Format the time to display as MM:SS
  const formattedTime = () => {
    const minutes = Math.floor(timeLeft / 60);
    const seconds = timeLeft % 60;
    return `${minutes < 10 ? '0' : ''}${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };
    return(
        <section id="countdown-section" style={{textAlign: "center"}}>
            <h1>Time is running out.<br/>Grab your spot fast!</h1>
            <h1>{d.getHours()} : {d.getMinutes()} : {d.getSeconds()}</h1>
            <Button width={"35%"} height={70}>
                    Register Now at &#8377; 499 <del>1999</del>
                </Button>
        </section>
    )
}

export default CountdownSection;
